import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';


/*
npm install axios

npm install react-bootstrap bootstrap
*/


function App() {

  const [highBid, setHighBid] = useState([]);
  useEffect(() => {
    axios.get('http://127.0.0.1:5000/getHighestBid')
      .then(response => {
        setHighBid(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);


  const [bids, setBids] = useState([]);
  useEffect(() => {
    axios.get('http://127.0.0.1:5000/getBids')
      .then(response => {
        setBids(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);


  const handleSubmit = event => {

    // get current high bid
    axios.get('http://127.0.0.1:5000/getHighestBid')
    .then(response => {
      setHighBid(response.data);
    })
    .catch(error => {
      console.error(error);
    });


    event.preventDefault();
    var url = 'http://127.0.0.1:5000/bid/' + highBid.Price+1 + '/Jason';
    console.log(url);
    axios.get(url)
      .then(response => {
        setHighBid(response.data);
      })
      .catch(error => {
        console.error(error);
      });

  }

  return (
    <div>

    <div>
      <table>
        <th>
        <button onClick={handleSubmit}>Bid</button>
        </th>
        <th>
        <p>{highBid.Price+1}</p>
        </th>
      </table>
    </div>

    <div class="container">
    <table>
      <tr>
        <th>Bidder</th> <th>Price</th>
      </tr>
      {bids.map(bid => (
        <tr>
          <th>{bid.Name}</th> <th>{bid.Price}</th>
        </tr>
      ))}
    </table>
    </div>

    </div>
  );
}

export default App;
