import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';


/*
npm install axios
npm install react-bootstrap bootstrap
*/


function App() {

  const [highBid, setHighBid] = useState([]);
  const [bids, setBids] = useState([]);
  const [name, setName] = useState([]);

  useEffect(() => {
    setName(JSON.parse(window.localStorage.getItem('name')));
  }, []);

  useEffect(() => {
    // storing input name
    localStorage.setItem("name", JSON.stringify(name));
  }, [name]);



  useEffect(() => {
    axios.get('http://127.0.0.1:5000/getHighestBid')
      .then(response => {
        setHighBid(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/getBids')
      .then(response => {
        setBids(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleSubmit = event => {
    event.preventDefault();

    // get current high bid
    axios.get('http://127.0.0.1:5000/getHighestBid')
    .then(response => {
      setHighBid(response.data);
    })
    .catch(error => {
      console.error(error);
    });

    // POST high bid
    let config = {
      headers: {
        "Content-Type": "application/json",
        'Access-Control-Allow-Origin': '*',
        }
    }
    let data = {"bid": highBid.Price+1, "name": name};
    var url = 'http://127.0.0.1:5000/bid';
    axios.post(url, data, config)
      .then(response => {
        if (name.length == 0) {
          throw("name cannot be empty")
        }
        console.log('success');
        window.location.reload();
        setName(data["name"]);
      })
      .catch(error => {
        console.error(error);
      });
  }

  return (
    <div>

    <div>
      <table>
        <th>

        <form>
          <label>Name: 
          <input
            type="text" 
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          </label>
        </form>

        </th>

        <th>
        <p>{highBid.Price+1}</p>
        </th>


        <th>
        <button onClick={handleSubmit}>Bid</button>
        </th>
      </table>
    </div>

    <div>
    <table>
      <tr>
        <th>Bidder</th> <th>Price</th>
      </tr>
      {bids.map(bid => (
        <tr>
          <th>{bid.Name}</th> <th>{bid.Price}</th>
        </tr>
      ))}
    </table>
    </div>

    </div>
  );
}

export default App;
