import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';


/*
npm install axios
npm install react-bootstrap bootstrap
*/




function App() {

  const [highBid, setHighBid] = useState([]);
  const [bids, setBids] = useState([]);
  const [name, setName] = useState("");
  const [time, setTime] = useState(Date.now());




  useEffect(() => {
    setName(localStorage.getItem("name", name));
  }, []);

  useEffect(() => {
    // storing input name
    localStorage.setItem("name", name);
  }, [name]);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/getHighestBid')
      .then(response => {
        setHighBid(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/getBids')
      .then(response => {
        setBids(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleSubmit = event => {
    event.preventDefault();

    // get current high bid
    axios.get('http://127.0.0.1:5000/getHighestBid')
    .then(response => {
      setHighBid(response.data);
    })
    .catch(error => {
      console.error(error);
    });

    // POST high bid
    let config = {
      headers: {
        "Content-Type": "application/json",
        'Access-Control-Allow-Origin': '*',
        }
    }
    let data = {"bid": highBid.Price+1, "name": name};
    var url = 'http://127.0.0.1:5000/bid';
    axios.post(url, data, config)
      .then(response => {
        if (name.length == 0) {
          throw("name cannot be empty")
        }
        console.log('success');

        // refresh bids list
        axios.get('http://127.0.0.1:5000/getBids')
            .then(response => {
              setBids(response.data);
            })
            .catch(error => {
              console.error(error);
            });

        // refresh highest bid
        axios.get('http://127.0.0.1:5000/getHighestBid')
          .then(response => {
            setHighBid(response.data);
          })
          .catch(error => {
            console.error(error);
          });

      })
      .catch(error => {
        console.error(error);
      });
  }

  return (
    <div>

    <div>
      <table>
        <th>

        <form>
          <label>Name: 
          <input
            type="text" 
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          </label>
        </form>

        </th>

        <th>
        <p>{highBid.Price+1}.00</p>
        </th>


        <th>
        <button onClick={handleSubmit}>Bid</button>
        </th>
      </table>
    </div>

    <div>
    <table>
      <tr>
        <th><h3>Bidder</h3></th> <th><h3>Price</h3></th>
      </tr>
      {bids.map(bid => (
        <tr>
          <th>{bid.Name}</th> <th>${bid.Price}.00</th>
        </tr>
      ))}
    </table>
    </div>

    </div>
  );
}

export default App;
